import React from 'react';
import iconFacebook from '../images/icon-facebook.svg';
import iconInstagram from '../images/icon-instagram.svg';
import iconTwitter from '../images/icon-twitter.svg';
import iconYoutube from '../images/icon-youtube.svg';
import iconUp from '../images/icon-up.svg';
import iconDown from '../images/icon-down.svg';

function OverviewCards(props) {
    const mediaName = props.mediaName;

    let icon = '';
    if (mediaName === "facebook") { icon = iconFacebook; }
    else if (mediaName === "instagram") { icon = iconInstagram; }
    else if (mediaName === "twitter") { icon = iconTwitter; }
    else if (mediaName === "youtube") { icon = iconYoutube; }

    let iconUpDownV =props.viewsRetweetsRate < 0? iconDown:iconUp;
    let iconUpDownL =props.likesRate < 0? iconDown:iconUp;
    let numUpDownV =props.viewsRetweetsRate.toString().replace('-','');
    let numUpDownL =props.likesRate.toString().replace('-','');


    return (
        <div className={"overviewCards "+mediaName+"-grid"}  >
            <div className="card small views">
               <span className='blue-text'>Page Views</span> <img src={icon} alt='icon-overview-view' />
                {props.viewsRetweets}
                <img src={iconUpDownV} /><span className={numUpDownV<0?'green':'red'}>{numUpDownV}%</span>
            </div>
            <div className="card small likes">
            <span className='blue-text'>Likes</span> <img src={icon} alt='icon-overview-likes' />
                {props.likes}
                <img src={iconUpDownL} /><span style={iconUpDownL===iconDown?{color:"hsl(356, 69%, 56%)"}:{color:"hsl(163, 72%, 41%)"}}>{numUpDownL}%</span>
            </div>
        </div>);
}

export default OverviewCards;
