import React, { useState } from 'react';

function Header() {
  const [isDarkMode, setIsDerkMode] = useState(false);
  const cards = [...document.querySelectorAll('.card')];
  const blueTexts = [...document.querySelectorAll('.blue-text')];

  function darkModeToggle() {
    setIsDerkMode(!isDarkMode);
    //    document.body.style=(isDarkMode ? 'background-color: hsl(225, 100%, 98%);' : 'background-color: hsl(230, 17%, 14%);');
  }

  if (isDarkMode) {
    //body
    document.body.classList.add('body-dark');
    document.body.classList.remove('body-light');

    //Card
    cards.map(card=>card.style='background-color:hsl(228, 28%, 20%)');
    blueTexts.map(blueText=>blueText.style='hsl(228, 34%, 66%)');


  } else {
    document.body.classList.remove('body-dark');
    document.body.classList.add('body-light');
  }

  return (
    <header>
      <h1>Social Media Dashboard</h1>
      <span className='blue-text'>Total Followers: 23,004</span>

      <div className="dark-mode blue-text">Dark Mode
        <label className="swich">
          <input type="checkbox" checked={isDarkMode} onChange={darkModeToggle} />
          <span className="slider round"></span>
        </label>
      </div>
    </header>);


}

export default Header;
