import React from 'react';
import iconFacebook from '../images/icon-facebook.svg';
import iconInstagram from '../images/icon-instagram.svg';
import iconTwitter from '../images/icon-twitter.svg';
import iconYoutube from '../images/icon-youtube.svg';
import iconUp from '../images/icon-up.svg';
import iconDown from '../images/icon-down.svg';

function OverviewCards(props) {
    const mediaName = props.mediaName;
    const style = {
        display: "flex",
        flexDirection: "row"
    };
    let icon = '';
    if (mediaName === "facebook") { icon = iconFacebook; }
    else if (mediaName === "instagram") { icon = iconInstagram; }
    else if (mediaName === "twitter") { icon = iconTwitter; }
    else if (mediaName === "youtube") { icon = iconYoutube; }

    return (<div className='overviewCards' styles={style}><div className="card small">
        Page Views <img src={icon} alt='icon-overview-view' />
        {props.viewsRetweets}
        <img src="images/icon-up.svg" />{props.viewsRetweetsRate}%
    </div>
        <div className="card small">
            Likes <img src={icon} alt='icon-overview-likes' />
            {props.likes}
            <img src="images/icon-down.svg" />{props.likesRate}%
      </div></div>);
}

export default OverviewCards;
