import React from 'react';
import Header from './Header';
import Footer from './Footer';
import MainCard from './MainCard';
import OverviewCard from './OverviewCard';
import media_data from '../media_data';


function App() {
  return (
    <div >
      <div className="page">
        <Header />
        <div className="main-dashboard">
          {media_data.map(data => <MainCard
            key={data.media_name}
            id={data.id}
            followers={data.followers}
            mediaName={data.media_name}
            upDownNum={data.upDown_num}
          />

          )}

        </div>

        <h2>Overview - Today</h2>
        <div className="overview">
        {media_data.map(data => <OverviewCard key={data.media_name}
            followers={data.followers}
            mediaName={data.media_name}
            upDownNum={data.upDown_num}/>
          )} 
          <div className="card small">
            Likes
            52
        <img src="images/icon-down.svg" />2%
      </div>
          <div className="card small">
            Likes
            5462
        <img src="images/icon-up.svg" />2257%
      </div>
          <div className="card small">
            Profile Views
            52k
        <img src="images/icon-up.svg" />1375%
      </div>
          <div className="card small">
            Retweets
            117
        <img src="images/icon-up.svg" />303%
      </div>
          <div className="card small">
            Likes
            507
        <img src="images/icon-up.svg" />553%
      </div>
          <div className="card small">
            Likes
            107
        <img src="images/icon-down.svg" />19%
      </div>
          <div className="card small">
            Total Views
            1407
        <img src="images/icon-down.svg" />12%
      </div>
        </div>
      </div>
      <Footer />


    </div>
  );
}

export default App;
